mapper.select:
	   select count(id)
	   from sys_user
	   where n_deleted=0
	    and c_username like CONCAT('%',#{user.c_username},'%')
   	    and c_phone like CONCAT('%',#{user.c_phone},'%')
   	   	and n_age = #{user.n_age}
   	     and n_sex = #{user.n_sex}
 	   	and substr(c_createDate,1,10) = #{user.c_createDate}
   	     and substr(c_updateDate,1,10) = #{user.c_updateDate}

mapper.update:
UPDATE sys_user SET
c_pwd = #{user.c_pwd},
c_phone = #{user.c_phone}, n_age = #{user.n_age}, n_sex = #{user.n_sex}, c_email = #{user.c_email}, c_username = #{user.c_username}, c_updateDate = #{user.c_updateDate}, n_updater = #{user.n_updater}, n_departmentId = #{user.n_departmentId.id} WHERE id = #{user.id}
